(function() {
  var fs, yaml, _;
  yaml = require('yaml');
  fs = require('fs');
  _ = require('underscore');
  exports.defaults = yaml.eval(String(fs.readFileSync('/Users/cwilcox/Projects/piles.io/settings.yml')));
  exports.development = _.extend({}, exports.defaults);
  exports.production = _.extend({}, exports.defaults, yaml.eval(String(fs.readFileSync('/Users/cwilcox/Projects/piles.io/settings_production.yml'))));
  exports.env = function(envName) {
    console.log("Settings using environment '" + envName + "'");
    return exports.settings = exports[envName];
  };
}).call(this);
